mapper.select:
SELECT so.id , so.ap_id apId, sa.name apname, su.username userName, st.name roomType, CASE so.`status` WHEN 1 THEN '已下订' WHEN 2 THEN '已入住' WHEN 3 THEN '退订中' WHEN 4 THEN '已退订' ELSE '订单完成' END as `status` , if(so.coupon_id is null,'无',sc.`name`) couponName, so.price , CONCAT_WS('至',so.in_time,so.out_time) liveTime, so.create_time orderTime FROM se_order so, sys_user su, se_apartment sa, se_coupon sc, se_tag st WHERE so.ap_id = sa.id AND so.user_id = su.id AND sa.room_type = st.id and so.coupon_id = sc.id
AND so.status = #{status}
AND so.user_id = #{userId}
AND ( su.username like CONCAT('%',#{remark},'%' ) or sa.`name` like CONCAT('%',#{remark},'%' ) or sc.`name` like CONCAT('%',#{remark},'%' ) or so.price like CONCAT('%',#{remark},'%' ) )
AND so.create_time >= #{startTime}
AND so.create_time <= #{endTime}
ORDER BY so.create_time desc

SELECT so.id id, su.username userName, so.ap_id apId, sa.`name` apName, sa.type type, ss.name shopName, ss.address shopAddress, sa.ap_num apNum, st.`name` roomType, so.creator_phone phone, st1.remark, CASE so.`status` WHEN 1 THEN '已下订' WHEN 2 THEN '退订中' WHEN 3 THEN '已退订' ELSE '订单完成' END as `status` , so.price , CONCAT_WS('至',so.in_time,so.out_time) liveTime, so.create_time orderTime, sol.liver , sol.phone, sol.id_card idCard FROM se_order so, sys_user su, se_apartment sa, se_coupon sc, se_order_liver sol, se_shop ss, se_tag st, se_tag st1
WHERE so.ap_id = sa.id and sa.shop_id = ss.id AND so.user_id = su.id and so.coupon_id = sc.id AND sol.order_id = so.id and sa.room_type = st.id and so.price_tag_id = st1.id
AND so.id = #{orderId}
